deployment.makefile:
# Makefile ‚Äì ROOT LEVEL (para ejecuci√≥n por Codex, Vercel y agentes)

# Despliegue completo con control de errores
deploy:
	@echo "üöÄ Desplegando proyecto completo..."
	npm install || { echo "‚ùå Error en instalaci√≥n de dependencias"; exit 1; }
	npm run build || { echo "‚ùå Error en build del proyecto"; exit 1; }
	@if grep -q 'next export' package.json; then echo "üì¶ Exportando proyecto..."; npm run export || { echo "‚ùå Error en export"; exit 1; }; fi
	npx vercel --prod || { echo "‚ùå Error al desplegar en Vercel"; exit 1; }
	@echo "‚úÖ Proyecto desplegado correctamente."

# Registro en Notion (requiere script y variables definidas)
notion:
	@echo "üìù Registrando en Notion..."
	node scripts/deploy_and_log.js || { echo "‚ùå Error al registrar en Notion"; exit 1; }

# Publicar en Shopify y activar trigger externo
shopify:
	@echo "Ô∏è Publicando producto en Shopify..."
	node scripts/upload_to_shopify.js || { echo "‚ùå Error al subir a Shopify"; exit 1; }
	curl -X POST -H 'Content-Type: application/json' -d '{}' https://tu-servidor.onrender.com/upload || { echo "‚ùå Error al activar trigger"; exit 1; }

# Ejecutar todo de forma secuencial
all: deploy notion shopify

.PHONY: deploy notion shopify all
